import React from "react";

import Chart from "../Chart/Chart.js";

const ExpensesChart = (props) => {
  const chartDataPoints = [
    { label: "Jan", value: 0 },
    { label: "Feb", value: 0 },
    { label: "Mar", value: 0 },
    { label: "Apr", value: 0 },
    { label: "May", value: 0 },
    { label: "Jun", value: 0 },
    { label: "Jul", value: 0 },
    { label: "Aug", value: 0 },
    { label: "Sep", value: 0 },
    { label: "Oct", value: 0 },
    { label: "Nov", value: 0 },
    { label: "Dec", value: 0 },
  ];

  const data = props.expenses.forEach((expense) => {
    const expenseMonth = expense.date.getMonth(); // staring az 0 (jan = 0)
    return (chartDataPoints[expenseMonth].value += expense.amount); // adding every expense amount to its epecific month in chartDataPoints array
  });

  return <Chart dataPoints={chartDataPoints} />;
};

export default ExpensesChart;
